import { textAlign } from '@mui/system';
import { useState } from 'react'

import './Calendar.css'

const getWeeks = (year: number, month: number): Date[][] => {
    const result = [];

    const firstDay = new Date(year, month-1, 1);
    const lastDay = new Date(year, month, 0);

    let tracker = firstDay;

    while (tracker.getDay() > 0)
      tracker.setDate(tracker.getDate() - 1);

    let week = [];
    while (tracker <= lastDay) {
      week.push(new Date(tracker));

      tracker.setDate(tracker.getDate() + 1)

      if (week.length === 7) {
        result.push(week);
        week = [];
      }
    }

    if (lastDay.getDay() < 6) {
      for (let i = tracker.getDay(); i < 7; i++) {
        week.push(new Date(tracker));
        tracker.setDate(tracker.getDate() + 1);
      }
      result.push(week);
    }

    return result;
}



export default function Calendar() {

  const defaultDate = new Date();

  const [monthSelected, setMonthSelected] = useState(defaultDate.getMonth()+1);
  const [yearSelected, setYearSelected] = useState(defaultDate.getFullYear());

  return (
    <div>
      <table>
        <thead>
          <tr className='calendarMonthHeader'>
            <th style={{textAlign: 'left'}} colSpan={5}>
              {monthSelected.toLocaleString()} {yearSelected}
            </th>
            <th colSpan={1}>
              <button>
                &lt
              </button>
            </th>
            <th colSpan={1}>
              <button>
                &gt
              </button>
            </th>
          </tr>
          <tr>
            <th>S</th>
            <th>M</th>
            <th>T</th>
            <th>W</th>
            <th>T</th>
            <th>F</th>
            <th>S</th>
          </tr>
        </thead>
        <tbody>
          {
            getWeeks(monthSelected, monthSelected+1)
            .map((dateRow: Date[], i: number): any => (
              <tr className=''>
                {dateRow.map((day: Date): any => {
                  return <td>
                    <button className='dateButton'>
                      { day.getDate() }
                    </button>
                  </td>
                })}
              </tr>
            ))
          }
        </tbody>
      </table>
    </div>
  )
}
