import React from 'react';
import { Table, TableBody, TableContainer, TableHead, TableRow, TableCell, Paper } from '@mui/material'
import { useState } from 'react'

const getWeeks = (year: number, month: number): any => {
  const result = [];

  const firstDay = new Date(year, month-1, 1);
  const lastDay = new Date(year, month, 0);

  let tracker = firstDay;

  while (tracker.getDay() > 0)
    tracker.setDate(tracker.getDate() - 1);

  let week = [];
  while (tracker <= lastDay) {
    week.push(new Date(tracker));

    tracker.setDate(tracker.getDate() + 1)

    if (week.length === 7) {
      result.push(week);
      week = [];
    }
  }

  return result;
}

function MainPage() {

  const [monthSelected, setMonthSelected] = useState(new Date());

  return (
    <div>
        <TableContainer component={ Paper }>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Sunday</TableCell>
                <TableCell>Sunday</TableCell>
                <TableCell>Sunday</TableCell>
                <TableCell>Sunday</TableCell>
                <TableCell>Sunday</TableCell>
                <TableCell>Sunday</TableCell>
                <TableCell>Sunday</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {
                getWeeks(monthSelected.getFullYear(), monthSelected.getMonth()+1).map((dateRow: number, i: number) => {
                  <p>{dateRow}</p>
                })
              }
            </TableBody>
          </Table>
        </TableContainer>
    </div>
  );
}

export default MainPage;
