import { useState, useEffect, useRef } from 'react'

import Calendar from './Calendar'
import Appointment from './Appointment'
import './Panel.css'

interface Props {
  setShowContactPanel: Function;
}

export default function Panel(props: Props) {

  const [selectedDate, setSelectedDate] = useState(new Date())

  const ref = useRef(null)

  useEffect(() => {

    // Function for click event
    function handleOutsideClick(event) {
      if (ref.current && !ref.current.contains(event.target)) {
        alert("you just clicked outside of box!");
      }
    }

    // Adding click event listener
    document.addEventListener("click", handleOutsideClick);
    return () => document.removeEventListener("click", handleOutsideClick);
  }, [ref]);

  return (
    <div className='mainPanel' onClick={() => props.setShowContactPanel(false)}>
      <div className='panel'>
        <div className='panelChild description'>
          <h5 style={{marginTop: "15px", fontSize: "24px"}}>Book a meeting with me</h5>
          <button>
            15 min
          </button>
          <button>
            30 min
          </button>
          <button>
            1 hour
          </button>
          <br></br>
          <p>Meeting context:</p>
          <input>
          {/* Description */}
          </input>
        </div>

        <div className='panelChild'>
          <Calendar setSelectedDate={setSelectedDate}/>
        </div>

        <div className='panelChild'>
          <Appointment selectedDate={selectedDate}/>
        </div>
      </div>
    </div>
  )
}
