import React from 'react'
import { Table, TableBody, TableContainer, TableHead, TableRow, TableCell, Paper } from '@mui/material'
import { useState } from 'react'

import Week from '../../components/Week/Week'

const getWeeks = (year: number, month: number): Date[][] => {
    const result = [];

    const firstDay = new Date(year, month-1, 1);
    const lastDay = new Date(year, month, 0);

    let tracker = firstDay;

    while (tracker.getDay() > 0)
      tracker.setDate(tracker.getDate() - 1);

    let week = [];
    while (tracker <= lastDay) {
      week.push(new Date(tracker));

      tracker.setDate(tracker.getDate() + 1)

      if (week.length === 7) {
        result.push(week);
        week = [];
      }
    }

    if (lastDay.getDay() < 6) {
      for (let i = tracker.getDay(); i < 7; i++) {
        week.push(new Date(tracker));
        tracker.setDate(tracker.getDate() + 1);
      }
      result.push(week);
    }

    return result;
}

export default function Calendar() {
  const [monthSelected, setMonthSelected] = useState(new Date());

  return (
    <div>
        <TableContainer sx={{width:1/2, }} component={ Paper }>
          <Table size='small' sx={{tableLayout: 'fixed'}} aria-label='simple table'>
            <TableHead>
              <TableRow>
                <TableCell align='center'>S</TableCell>
                <TableCell align='center'>M</TableCell>
                <TableCell align='center'>T</TableCell>
                <TableCell align='center'>W</TableCell>
                <TableCell align='center'>T</TableCell>
                <TableCell align='center'>F</TableCell>
                <TableCell align='center'>S</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {
                getWeeks(monthSelected.getFullYear(), monthSelected.getMonth()+1)
                .map((dateRow: Date[], i: number): any => (
                  <Week key={i} week={dateRow}/>
                ))
              }
            </TableBody>
          </Table>
        </TableContainer>
    </div>
  )
}
